INSERT INTO `wp_wc_admin_notes` VALUES (30,'tap_to_pay_iphone_q2_2023_no_wcpay','marketing','en_US','Accept in-person contactless payments on your iPhone','Tap to Pay on iPhone and WooCommerce Payments is quick, secure, and simple to set up — no extra terminals or card readers are needed. Accept contactless debit and credit cards, Apple Pay, and other NFC digital wallets in person.','{}','pending','woocommerce.com','2024-04-13 11:26:40',NULL,0,'plain','',0,0,'info');
